mapper.select:
select wr.name,wcr.contact_id
from `mc_work_contact_room` wcr
JOIN `mc_work_room` wr ON wcr.room_id=wr.id

   wcr.status=1


           and wcr.contact_id IN

               #{contactIdItem}



           and wcr.contact_id=#{contactIds[0]}



        AND wcr.room_id IN

            #{roomIdItem}

mapper.update:
update mc_work_contact_room

    out_time=#{outTime}
    status=#{status}

where id=#{id}
